mapper.select:
SELECT 

FROM corp_account_resetpass_application a

WHERE a.id = #{id}

SELECT
FROM corp_account_resetpass_application a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM corp_account_resetpass_application a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE corp_account_resetpass_application SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
